// The package @fortawesome/vue-fontawesome is currently lacking types for typescript
// So we use this as a workaround - https://github.com/FortAwesome/vue-fontawesome/issues/24
declare module '@fortawesome/vue-fontawesome';

// Quasar is also lacking typescript types
declare module 'quasar';
declare module 'quasar/icon-set/mdi-v5';
